INSERT INTO `RequirementConfig` VALUES ('1129', '', '9', '    2. Panning a camera', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1130', '', '10', '    3. Framing a shot', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1131', '', '11', '    4. Selecting an angle', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1132', '', '12', '    5. Selecting proper lighting', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1133', '', '13', '    6. Hand-held shooting ', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1134', '', '14', '  d. Using motion picture techniques, plan ONE of the following programs. Start with a treatment, and complete the requirement by presenting this program to a pack or your troop, patrol, or class.', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1135', '', '15', '    1. Film or videotape a court of honor and show it to an audience.', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1136', '', '16', '    2. Create a short feature of your own design using the techniques you learned.', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1137', '', '17', '    3. Shoot a vignette that could be used to train a new Scout in a Scouting skill. ', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1138', '', '18', '3. Do ONE of the following:', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1139', '', '19', '    1. With your parent\'s permission and your counselor\'s approval, visit a film set or a television production studio and watch how production work is done.', '42', '');
INSERT INTO `RequirementConfig` VALUES ('1140', '', '20', '    2. Explain to your counselor the elements of the zoom lens and three important parts. ', '42', '');
