INSERT INTO `RequirementConfig` VALUES ('1142', '', '1', '1. Do the following:', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1143', '', '2', '  a. Show that you know first aid for and how to prevent injuries or illnesses that could occur during climbing activities, including heat and cold reactions, dehydration, stopped breathing, sprains, abrasions, fractures, rope burns, blisters, snakebite, and insect bites or stings. ', '43', '');
