INSERT INTO `RequirementConfig` VALUES ('1165', '', '24', '  b. Figure eight follow-through ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1166', '', '25', '  c. Water knot ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1167', '', '26', '  d. Double fisherman\'s knot (grapevine knot) ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1168', '', '27', '  e. Safety knot ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1169', '', '28', '8. Harnesses. Correctly put on at least ONE of the following:', '43', '');
