INSERT INTO `RequirementConfig` VALUES ('1171', '', '30', '  b. Tied harness ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1172', '', '31', '9. Belaying. Do the following:', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1173', '', '32', '  a. Explain the importance of belaying climbers and rappellers and when it is necessary. ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1174', '', '33', '  b. Belay three different climbers ascending a rock face or climbing wall. ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1175', '', '34', '  c. Belay three different rappellers descending a rock face or climbing wall using a top rope. ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1176', '', '35', '10. Climbing. Do the following:', '43', '');
