INSERT INTO `RequirementConfig` VALUES ('1178', '', '37', '  b. Climb at least three different routes on a rock face or climbing wall, demonstrating good technique and using verbal signals with a belayer. ', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1179', '', '38', '11. Rappelling. Do the following:', '43', '');
INSERT INTO `RequirementConfig` VALUES ('1180', '', '39', '  a. Using a carabiner and a rappel device, secure your climbing harness to a rappel rope. ', '43', '');
