INSERT INTO `RequirementConfig` VALUES ('3478', '', '10', '  3. Explain the importance of safety equipment such as PFDs, air bags, grab loops, and helmets.', '152', '');
INSERT INTO `RequirementConfig` VALUES ('3479', '', '11', '4. Demonstrate your ability to aid yourself and others in the event of a capsize:', '152', '');
