INSERT INTO `RequirementConfig` VALUES ('4257', '', '1', '1. Faith in God Award Requirements', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4258', '', '2', 'Basic Requirements An important part of faith in God is keeping your baptismal covenant and staying spiritually strong. To help you develop faith and resist temptation, do the following things:', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4259', '', '3', '  A. Pray daily to Heavenly Father.', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4260', '', '4', '  B. Read the scriptures regularly.', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4261', '', '5', '  C. Keep the commandments and live \'My Gospel Standards\' (see back cover).', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4262', '', '6', '  D. Honor your parents and be kind to your family.', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4263', '', '7', '  E. Pay your tithing and attend tithing settlement.', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4264', '', '8', '  F. Attend sacrament meetings and Primary regularly.', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4265', '', '9', 'Other Requirements: Doing the following activities will help your understanding and testimony of the gospel grow:', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4266', '', '10', '  A. Write your testimony.', '499', '');
INSERT INTO `RequirementConfig` VALUES ('4267', '', '11', '  B. Memorize the Articles of Faith and explain what they mean.', '499', '');
