INSERT INTO `RequirementConfig` VALUES ('4614', '', '70', '  f. Make a poster for a Cub Scout project or a pack meeting.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4615', '', '71', '13. BIRDS (Page 174)\r\n   This elective is also part of the World Conservation Award.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4616', '', '72', '  a. Make a list of all the birds you saw in a week and tell where you saw them (field, forest, marsh, yard, or park).', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4617', '', '73', '  b. Put out nesting material (short pieces of yarn and string) for birds and tell which birds might use it.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4618', '', '74', '  c. Read a book about birds.', '504', '');
