INSERT INTO `RequirementConfig` VALUES ('4628', '', '84', '  a. Plant and raise a box garden.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4629', '', '85', '  b. Plant and raise a flower bed.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4630', '', '86', '  c. Grow a plant indoors.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4631', '', '87', '  d. Plant and raise vegetables.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4632', '', '88', '  e. Visit a botanical garden or other agricultural exhibition in your area.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4633', '', '89', '16. FAMILY ALERT (Page 188)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4634', '', '90', '  a. Talk with your family about what you will do in an emergency.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4635', '', '91', '  b. In case of a bad storm or flood, know where you can get safe food and water in your home. Tell how to purify water. Show one way. Know where and how to shut off water, electricity, gas, or oil.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4636', '', '92', '  c. Make a list of your first aid supplies, or make a first aid kit. Know where the first aid things are kept.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4637', '', '93', '17. TIE IT RIGHT (Page 190)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4638', '', '94', '  a. Learn to tie an overhand knot and a square knot.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4639', '', '95', '  b. Tie your shoelaces with a square bow knot.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4640', '', '96', '  c. Wrap and tie a package so that it is neat and tight.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4641', '', '97', '  d. Tie a stack of newspapers the right way.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4642', '', '98', '  e. Tie two cords together with an overhand knot.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4643', '', '99', '  f. Learn to tie a necktie.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4644', '', '100', '  g. Wrap the end of a rope with tape to keep it from unwinding.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4645', '', '101', '18. OUTDOOR ADVENTURE (Page 196)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4646', '', '102', '  a. Help plan and hold a picnic with your family or den.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4647', '', '103', '  b. With an adult, help plan and run a family or den outing.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4648', '', '104', '  c. Help plan and lay out a treasure hunt something like this.  (Example map shown in book.)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4650', '', '106', '  d. Help plan and lay out an obstacle race. Use this idea or make up your own.  (Example list shown in book.)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4653', '', '109', '  e. Help plan and lay out an adventure trail.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4654', '', '110', '  f. Take part in two summertime pack events with your den.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4655', '', '111', '  g. Point out poisonous plants. Tell what to do if you accidentally touch one of them.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4656', '', '112', '19. FISHING (Page 200)\r\n   This elective is also part of the Cub Scout World Conservation Award', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4657', '', '113', '  a. Identify five different kinds of fish.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4658', '', '114', '  b. Rig a pole with the right kind of line and hook. Attach a bobber and sinker, if you need them. Then go fishing.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4659', '', '115', '  c. Fish with members of your family or an adult. Bait your hook and do your best to catch a fish.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4660', '', '116', '  d. Know the rules of safe fishing.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4661', '', '117', '  e. Tell about some of the fishing laws where you live.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4662', '', '118', '  f. Show how to use a rod and reel.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4663', '', '119', '20. SPORTS (Page 204)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4664', '', '120', '  a. Play a game of tennis, table tennis, or badminton.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4665', '', '121', '  b. Know boating safety rules.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4666', '', '122', '  c. Earn the Cub Scouting shooting sports Archery belt loop.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4667', '', '123', '  d. Understand the safety and courtesy code for skiing. Show walking and the kick turn. Do climbing with a side stop or herringbone. Show the snowplow or stem turn, and how to get up from a fall.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4668', '', '124', '  e. Know the safety rules for ice skating. Skate, without falling, as far as you can walk in 50 steps. Come to a stop. Turn from forward to backward.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4669', '', '125', '  f. In roller skating, know the safety rules. From a standing start, skate forward as far as you can walk in 50 steps. Come to a stop within 10 walking steps. Skate around a corner one way without coasting. Then do the same coming back. Turn from forward to backward.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4670', '', '126', '  g. Go bowling.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4671', '', '127', '  h. Show how to make a sprint start in track. See how far you can run in 10 seconds.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4672', '', '128', '  i. Do a standing long jump. Jump as far as you can.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4673', '', '129', '  j. Play a game of flag football.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4674', '', '130', '  k. Show how to dribble and kick a soccer ball.  Take part in a game.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4675', '', '131', '  l. Play a game of baseball or softball.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4676', '', '132', '  m. Show how to shoot, pass, and dribble a basketball. Take part in a game.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4677', '', '133', '  n. Earn the Cub Scouting shooting sports BB-gun shooting belt loop.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4678', '', '134', '  o. With your den, participate in four outdoor physical fitness-related activities.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4679', '', '135', '21. COMPUTERS (Page 216)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4680', '', '136', '  a. Visit a business where computers are used. Find out what the computers do.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4681', '', '137', '  b. Explain what a computer program does.  Use a program to write a report for school, to write a letter, or for something else.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4682', '', '138', '  c. Tell what a computer mouse is.  Describe how a CD-ROM is used.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4683', '', '139', '22. SAY IT RIGHT (Page 218)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4684', '', '140', '  a. Say \'hello\' in a language other than English.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4686', '', '142', '  b. Count to ten in a language other than English.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4687', '', '143', '  c. Tell a short story to your den, your den leader, or an adult.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4688', '', '144', '  d. Tell how to get to a nearby fire station or police station from your home, your den meeting place, and school. Use directions and street names.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4689', '', '145', '  e. Invite a boy to join Cub Scouting or help a new Cub Scout through the Bobcat trail.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4690', '', '146', '23. LET\'S GO CAMPING (Page 222)', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4691', '', '147', '  a. Participate with your pack on an overnight campout.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4692', '', '148', '  b. Explain the basics of how to take care of yourself in the outdoors.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4693', '', '149', '  c. Tell what to do if you get lost.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4694', '', '150', '  d. Explain the buddy system.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4695', '', '151', '  e. Attend day camp in your area.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4696', '', '152', '  f. Attend resident camp in your area.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4697', '', '153', '  g. Participate with your den at a campfire in front of your pack.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4698', '', '154', '  h. With your den or pack or family, participate in a worship service outdoors.', '504', '');
INSERT INTO `RequirementConfig` VALUES ('4699', '', '0', 'The Bear Cub Scout program is for boys who have completed second grade (or are age 9). After earning the Bobcat badge, a boy may earn the Bear badge by completing 12 achievements in four different categories: God, Country, Family, and Self.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4700', '', '1', 'GOD (Do ONE of the following)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4701', '', '2', '1. WAYS WE WORSHIP (Page 26)\r\n   Complete both requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4702', '', '3', '    a. Complete the Character Connection for Faith', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4703', '', '4', '      -Know. Name some people in history who have shown great faith. Discuss with an adult how faith has been important at a particular point in his or her life.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4704', '', '5', '      -Commit. Discuss with an adult how having faith and hope will help you in your life, and also discuss some ways that you can strengthen your faith.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4705', '', '6', '      -Practice. Practice your faith as you are taught in your home, church, synagogue, mosque, or religious fellowship.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4706', '', '7', '    b. Make a list of things you can do this week to practice your religion as you are taught in your home, church, synagogue, mosque, or other religious community. Check them off your list as you complete them.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4707', '', '8', '2. EMBLEMS OF FAITH (Page 30)\r\n   Complete the requirement.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4708', '', '9', '    a. Earn the religious emblem of your faith. (A list of the religious emblems available to Cub Scouts is listed on this site.  Click here to see it.)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4709', '', '10', 'COUNTRY (Do THREE of the following)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4710', '', '11', '3. WHAT MAKES AMERICA SPECIAL?  (Page 34)\r\n   (Do requirements (a) and (j) and any two of the other requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4711', '', '12', '    a. Write or tell what makes America special to you.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4712', '', '13', '    b. With the help of your family or den leader, find out about two famous Americans. Tell the things they did or are doing to improve our way of life.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4713', '', '14', '    c. Find out something about the old homes near where you live. Go and see two of them.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4714', '', '15', '    d. Find out where places of historical interest are located in or near your town or city. Go and visit one of them with your family or den.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4715', '', '16', '    e. Choose a state; it can be your favorite one or your home state. Name its state bird, tree, and flower. Describe its flag. Give the date it was admitted to the Union.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4716', '', '17', '    f. Be a member of the color guard in a flag ceremony for your den or pack.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4717', '', '18', '    g. Display the U.S. flag in your home or fly it on three national holidays..', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4718', '', '19', '    h. Learn how to raise and lower a U.S. flag properly for an outdoor ceremony.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4719', '', '20', '    i. Participate in an outdoor flag ceremony', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4720', '', '21', '    j. Complete the Character Connection for Citizenship.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4721', '', '22', '      -Know. Tell ways some people in the past have served our country. Tell about some people who serve our country today. (Don\'t forget about \'ordinary\' people who serve our country.)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4722', '', '23', '      -Commit. Tell something that might happen to you and your family if other people were not responsible citizens. Tell one thing you will do to be a good citizen.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4723', '', '24', '      -Practice. Tell three things you did in one week that show you are a good citizen.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4724', '', '25', '4. TALL TALES  (Page 42)\r\n   Do all three requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4725', '', '26', '    a. Tell in your own words what folklore is. List some folklore stories, folk songs, or historical legends from your own state or part of the country. Play the Folklore Match Game on page 48.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4726', '', '27', '    b. Name at least five stories about American folklore. Point out on a United States map where they happened.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4727', '', '28', '    c. Read two folklore stories and tell your favorite one to your den.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4728', '', '29', '5. SHARING YOUR WORLD WITH WILDLIFE (Page 50)\r\n   This elective is also part of the Cub Scout World Conservation Award\r\n   Do four of the requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4729', '', '30', '    a. Choose a bird or animal that you like and find out how it lives. Make a poster showing what you have learned.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4730', '', '31', '    b. Build or make a bird feeder or birdhouse and hang it in a place where birds can visit safely.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4731', '', '32', '    c. Explain what a wildlife conservation officer does.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4732', '', '178', '__', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4733', '', '34', '    d. Visit one of the following: Zoo, Nature center, Aviary, Wildlife refuge, Game preserve.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4734', '', '35', '    e. Name one animal that has become extinct in the last 100 years. Tell why animals become extinct. Name one animal that is on the endangered species list.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4735', '', '36', '6. TAKE CARE OF YOUR PLANET (Page 56)\r\n   Do three requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4736', '', '37', '    a. Save 5 pounds of glass or aluminum, or 1 month of daily newspapers. Turn them in at a recycling center or use your community\'s recycling service.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4737', '', '38', '    b. Plant a tree in your yard, or on the grounds of the group that operates your Cub Scout pack, or in a park or other public place. Be sure to get permission first.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4738', '', '39', '    c. Call city or county officials or your trash hauling company and find out what happens to your trash after it is hauled away.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4739', '', '40', '    d. List all the ways water is used in your home. Search for dripping faucets or other ways water might be wasted. With an adult, repair or correct those problems.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4740', '', '41', '    e. Discuss with an adult in your family the kinds of energy your family uses.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4741', '', '42', '    f. Find out more about your family\'s use of electricity.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4742', '', '43', '    g. Take part in a den or pack neighborhood clean-up project.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4743', '', '44', '7. LAW ENFORCEMENT IS A BIG JOB (Page 64)\r\n   Do all six requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4744', '', '45', '    a. Practice one way police gather evidence: by taking fingerprints, or taking shoeprints, or taking tire track casts.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4745', '', '46', '    b. Visit your local sheriff\'s office or police station or talk with a law enforcement officer visiting your den or pack to discuss crime prevention.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4746', '', '47', '    c. Help with crime prevention for your home.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4747', '', '48', '    d. Be sure you know where to get help in your neighborhood.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4748', '', '49', '    e. Learn the phone numbers to use in an emergency and post them by each phone in your home.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4749', '', '50', '    f. Know what you can do to help law enforcement.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4750', '', '51', 'FAMILY (Do FOUR of the following)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4751', '', '52', '8. THE PAST IS EXCITING AND IMPORTANT (Page 72)\r\n   Do requirement g and two other requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4752', '', '53', '    a. Visit your library or newspaper office. Ask to see back issues of newspapers or an almanac.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4753', '', '54', '    b. Find someone who was a Cub Scout a long time ago. Talk with him about what Cub Scouting was like then.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4754', '', '55', '    c. Start or add to an existing den or pack scrapbook.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4755', '', '56', '    d. Trace your family back through your grandparents or great-grandparents; or, talk to a grandparent about what it was like when he or she was younger.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4756', '', '57', '    e. Find out some history about your community.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4757', '', '58', '    f. Start your own history: keep a journal for 2 weeks.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4758', '', '59', '    g. Complete the Character Connection for Respect.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4759', '', '60', '      -Know. As you learn about how Cub Scout-age life was like for adults you know, does what you learn change what you think about them. Tell how it might help you respect or value them more.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4760', '', '61', '      -Commit. Can you think of reasons others might be disrespectful to people or things you value? Name one new way you will show respect for a person or thing someone else values.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4761', '', '62', '      -Practice. List some ways you can show respect for people and events in the past.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4762', '', '63', '9. WHAT\'S COOKING? (Page 80)\r\n   Do four requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4763', '', '64', '    a. With an adult, bake cookies.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4764', '', '65', '    b. With an adult, make snacks for the next den meeting.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4765', '', '66', '    c. With an adult, prepare one part of your breakfast, one part of your lunch, and one part of your supper.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4766', '', '67', '    d. Make a list of the \'junk foods\' you eat. Discuss \'junk food\' with a parent or teacher.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4767', '', '68', '    e. Make some trail food for a hike.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4768', '', '69', '    f. With an adult, make a dessert for your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4769', '', '70', '    g. With an adult, cook something outdoors.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4770', '', '71', '10. FAMILY FUN (Page 90)\r\n   Do both requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4771', '', '72', '    a. Go on a day trip or evening out with members of your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4772', '', '73', '    b. Have a family fun night at home.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4773', '', '74', '11. BE READY! (Page 06)\r\n   Do requirements a through e and requirement g. Requirement f  is recommended, but not required.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4774', '', '75', '    a. Tell what to do in case of an accident in the home. A family member needs help. Someone\'s clothes catch on fire.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4775', '', '76', '    b. Tell what to do in case of a water accident.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4776', '', '77', '    c. Tell what to do in case of a school bus accident.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4777', '', '78', '    d. Tell what to do in case of a car accident.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4778', '', '79', '    e. With your family, plan escape routes from your home and have a practice drill.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4779', '', '80', '    f. Have a health checkup by a physician (optional).', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4780', '', '81', '    g. Complete the Character Connection for Courage.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4781', '', '82', '      -Know. Memorize the courage steps: Be brave, Be calm, Be clear, and Be careful. Tell why each courage step is important. How will memorizing the courage steps help you to be ready?', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4782', '', '83', '      -Commit. Tell why it might be difficult to follow the courage steps in an emergency situation. Think of other times you can use the courage steps. (Standing up to a bully is one example.)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4783', '', '84', '      -Practice. Act out one of the requirements using these courage steps: Be brave, Be calm, Be clear, and Be careful.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4784', '', '85', '12. FAMILY OUTDOOR ADVENTURE (Page 106)\r\n   This achievement is also part of Cub Scouting\'s Leave No Trace Award\r\n   Do three requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4785', '', '86', '    a. Go camping with your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4786', '', '87', '    b. Go on a hike with your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4787', '', '88', '    c. Have a picnic with your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4788', '', '89', '    d. Attend an outdoor event with your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4789', '', '90', '    e. Plan your outdoor family day.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4790', '', '91', '13. SAVING WELL, SPENDING WELL (Page 112)\r\n   Do four requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4791', '', '92', '    a. Go grocery shopping with a parent or other adult member of your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4792', '', '93', '    b. Set up a savings account.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4793', '', '94', '    c. Keep a record of how you spend money for 2 weeks.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4794', '', '95', '    d. Pretend you are shopping for a car for your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4795', '', '96', '    e. Discuss family finances with a parent or guardian.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4796', '', '97', '    f. Play a board game with your family that involves the use of play money.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4797', '', '98', '    g. With an adult, figure out how much it costs for each person in your home to eat one meal.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4798', '', '99', 'SELF (do FOUR of the following)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4799', '', '100', '14. RIDE RIGHT (Page 118)\r\n   Do requirement (a) and three other requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4800', '', '101', '    a. Know the rules for bike safety. If your town requires a bicycle license, be sure to get one.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4801', '', '102', '    b. Learn to ride a bike, if you haven\'t by now. Show that you can follow a winding course for 60 feet doing sharp left and right turns, a U-turn, and an emergency stop.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4802', '', '103', '    c. Keep your bike in good shape. Identify the parts of a bike that should be checked often.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4803', '', '104', '    d. Change a tire on a bicycle.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4804', '', '105', '    e. Protect your bike from theft. Use a bicycle lock.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4805', '', '106', '    f. Ride a bike for 1 mile without rest. Be sure to obey all traffic rules.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4806', '', '107', '    g. Plan and take a family bike hike.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4807', '', '108', '15. GAMES, GAMES, GAMES! (Page 126)\r\n   Do two requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4808', '', '109', '    a. Set up the equipment and play any two of these outdoor games with your family or friends.\r\n     (Backyard golf, Badminton, Croquet, Sidewalk shuffleboard, Kickball, Softball, Tetherball, Horseshoes, Volleyball)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4809', '', '110', '    b. Play two organized games with your den.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4810', '', '111', '    c. Select a game that your den has never played. Explain the rules. Tell them how to play it, and then play it with them.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4811', '', '112', '16. BUILDING MUSCLES (Page 130)\r\n   Do all three requirements.\r\n   NOTE TO PARENTS: If a licensed physician certifies that the Cub Scout\'s physical condition for an indeterminable time doesn\'t permit him to do three of the requirements in this achievement, the Cubmaster and pack committee may authorize substitution of any three Arrow Point electives.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4812', '', '113', '    a. Do physical fitness stretching exercises. Then do curl-ups, push-ups, the standing long jump, and the softball throw.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4813', '', '114', '    b. With a friend about your size, compete in at least six different two-person contests. (Many examples in book.)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4814', '', '115', '    c. Compete with your den or pack in the crab relay, gorilla relay, 30-yard dash, and kangaroo relay.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4815', '', '116', '17. INFORMATION, PLEASE -  (Page 136)\r\n   Do requirement (a) and three more requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4816', '', '117', '    a. With an adult in your family, choose a TV show. Watch it together.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4817', '', '118', '    b. Play a game of charades at your den meeting or with your family at home.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4818', '', '119', '    c. Visit a newspaper office, or a TV or radio station and talk to a news reporter.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4819', '', '120', '    d. Use a computer to get information.  Write, spell-check, and print out a report on what you learned.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4820', '', '121', '    e. Write a letter to a company that makes something you use.  Use e-mail or the U.S. Postal Service.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4821', '', '122', '    f. Talk with a parent or other family member about how getting and giving facts fits into his or her job.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4822', '', '123', '18. JOT IT DOWN  (Page 140)\r\n   Do requirement h and four other requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4823', '', '124', '    a. Make a list of the things you want to do today. Check them off when you have done them.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4824', '', '125', '    b. Write two letters to relatives or friends.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4825', '', '126', '    c. Keep a daily record of your activities for 2 weeks.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4826', '', '127', '    d. Write an invitation to someone.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4827', '', '128', '    e. Write a thank-you note.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4828', '', '129', '    f. Write a story about something you have done with your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4829', '', '130', '    g. Write about the activities of your den.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4830', '', '131', '    h. Complete the Character Connection for Honesty.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4831', '', '132', '      -Know. Tell what made it difficult to be clear and accurate as you wrote details and kept records, and tell what could tempt you to write something that was not exactly true. Define honesty.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4832', '', '133', '      -Commit. Tell why it is important to be honest and trustworthy with yourself and with others. Imagine you had reported something inaccurately and tell how you could set the record straight. Give reasons that honest reporting will earn the trust of others.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4833', '', '134', '      -Practice. While doing the requirement for this achievement, be honest when you are writing about real events.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4834', '', '135', '19. SHAVINGS AND CHIPS (Page 146)\r\n   Do all four requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4835', '', '136', '    a. Know the safety rules for handling a knife.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4836', '', '137', '    b. Show that you know how to take care of and use a pocketknife.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4837', '', '138', '    c. Make a carving with a pocketknife. Work with your den leader or other adult when doing this.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4838', '', '139', '    d. Earn the Whittling Chip card.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4839', '', '140', '20. SAWDUST AND NAILS (Page 152)\r\n   Do all three requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4840', '', '141', '    a. Show how to use and take care of four of these tools (Hammer, Hand saw, Hand drill, C-clamp, Wood plane, Pliers, Crescent wrench, Screwdriver, Bench vise, Coping saw, Drill bit)', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4841', '', '142', '    b. Build your own tool box.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4842', '', '143', '    c. Use at least two tools listed in requirement (a) to fix something.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4843', '', '144', '21. BUILD A MODEL (Page 156)\r\n   Do requirement g and two other requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4844', '', '145', '    a. Build a model from a kit.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4845', '', '146', '    b. Build a display for one of your models.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4846', '', '147', '    c. Pretend you are planning to change the furniture layout in one of the rooms in your home.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4847', '', '148', '    d. Make a model of a mountain, a meadow, a canyon, or a river.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4848', '', '149', '    e. Go and see a model of a shopping center or new building that is on display somewhere.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4849', '', '150', '    f. Make a model of a rocket, boat, car, or plane.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4850', '', '151', '    g. Complete the Character Connection for Resourcefulness.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4851', '', '152', '      -Know. Review the requirements for this achievement and list the resources you would need to complete them. Then list the materials you could substitute for items that you do not already have. Tell what it means to be resourceful.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4852', '', '153', '      -Commit. After you complete the requirements for this achievement, list any changes that would make the results better if you did these projects again. Tell why it is important to consider all available resources for a project.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4853', '', '154', '      -Practice. While you complete the requirements for this achievement, make notes on which materials worked well in your projects and why.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4854', '', '155', '22. TYING IT ALL UP (Page 162)\r\n   Do five requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4855', '', '156', '    a. Whip the ends of a rope.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4856', '', '157', '    b. Tie a square knot, bowline, sheet bend, two half hitches, and slip knot.  Tell how each knot is used.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4857', '', '158', '    c. Learn how to keep a rope from tangling.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4858', '', '159', '    d. Coil a rope. Throw it, hitting a 2-foot square marker 20 feet away.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4859', '', '160', '    e. Learn a magic rope trick.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4860', '', '161', '    f. Make your own rope.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4861', '', '162', '23. SPORTS, SPORTS, SPORTS (Page 170)\r\n   Do all five requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4862', '', '163', '    a. Learn the rules of and how to play three team sports.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4863', '', '164', '    b. Learn the rules of and how to play two sports in which only one person is on each side.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4864', '', '165', '    c. Take part in one team and one individual sport.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4865', '', '166', '    d. Watch a sport on TV with a parent or some other adult member of your family.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4866', '', '167', '    e. Attend a high school, college, or professional sporting event with your family or your den.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4867', '', '168', '24. BE A LEADER (Page 174)\r\n   Do requirement f and two other requirements.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4868', '', '169', '    a. Help a boy join Cub Scouting, or help a new Cub Scout through the Bobcat trail.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4869', '', '170', '    b. Serve as a denner or assistant denner.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4870', '', '171', '    c. Plan and conduct a den activity with the approval of your den leader.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4871', '', '172', '    d. Tell two people they have done a good job.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4872', '', '173', '    e. Leadership means choosing a way even when not everybody likes your choice.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4873', '', '174', '    f. Complete the Character Connection for Compassion.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4874', '', '175', '      -Know. Tell why, as a leader, it is important to show kindness and concern for other people. List ways leaders show they care about the thoughts and feelings of others.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4875', '', '176', '      -Commit. Tell why a good leader must consider the ideas, abilities, and feelings of others. Tell why it might be hard for a leader to protect another person\'s well-being. Tell ways you can be kind and compassionate.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4876', '', '177', '      -Practice. While you complete the requirements for this achievement, find ways to be kind and considerate of others.', '505', '');
INSERT INTO `RequirementConfig` VALUES ('4877', '', '0', 'AFTER a Bear Cub Scout earns his Bear Badge he may begin earning Arrow Points in the Electives.\r\n\r\nHe may work on his (Arrow Point Trail) at any time, however he cannot receive Arrow Points until AFTER he has earned the Bear Badge.\r\n\r\nThere is a big difference in the achievements for arrow points for Bear. In this rank the Cub Scout can go back and do requirements from the ACHIEVEMENTS section of the book and use them as requirements for arrow points, as long as they do not count any requirements from achievements that they used to earn the Bear Badge. Unused parts of achievements that were used for the Bear badge may NOT be counted toward Arrow Points.\r\n\r\nThe Achievement requirements and the Elective requirements can be freely mixed to count toward earning arrow points. In the following descriptions, we will use the term (arrow point activities) to refer to either type of requirement.\r\n\r\nGOLD ARROW POINT:\r\n    For the FIRST 10 arrow point activities completed, the Bear Cub earns his GOLD ARROW POINT.\r\nSILVER ARROW POINTS:\r\n    For EACH 10 arrow point activities completed (AFTER HE EARNS THE GOLD ARROW POINT) the Bear Cub earns a SILVER ARROW POINT. \r\n\r\nAs a BEAR Cub Scout, a boy may earn any number of SILVER ARROW POINTS, but he may only earn ONE GOLD ARROW POINT for the first 10 arrow point activities that he completes.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4878', '', '1', '1. SPACE (Page 182)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4879', '', '2', '  a. Identify two constellations and the North Star in the night sky.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4880', '', '3', '  b. Make a pinhole planetarium and show three constellations.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4881', '', '4', '  c. Visit a planetarium.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4882', '', '5', '  d. Build a model of a rocket or space satellite.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4883', '', '6', '  e. Read and talk about at least one man-made satellite and one natural one.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4884', '', '7', '  f. Find a picture of another planet in our solar system. Explain how it is different from Earth.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4885', '', '8', '2. WEATHER (Page 184)\r\n   This elective is also part of the Cub Scout World Conservation Award.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4886', '', '9', '  a. Learn how to read an outdoor thermometer. Put one outdoors and read it at the same time every day for two weeks. Keep a record of each day\'s temperature and a description of the weather each day (fair skies, rain, fog, snow, etc.).', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4887', '', '10', '  b. Build a weather vane. Record wind direction every day at the same hour for two weeks. Keep a record of the weather for each day.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4888', '', '11', '  c. Make a rain gauge.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4889', '', '12', '  d. Find out what a barometer is and how it works. Tell your den about it. Tell what \'relative humidity\' means.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4890', '', '13', '  e. Learn to identify three different kinds of clouds. Estimate their heights.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4891', '', '14', '  f. Watch the weather forecast on TV every day for two weeks. Describe three different symbols used on weather maps. Keep a record of how many times the weather forecast is correct.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4892', '', '15', '3. RADIO (Page 190)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4893', '', '16', '  a. Build a crystal or diode radio. Check with your local craft or hobby shop or the nearest Scout shop that carries a crystal radio kit. It is all right to use a kit.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4894', '', '17', '  b. Make and operate a battery powered radio, following the directions with the kit.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4895', '', '18', '4. ELECTRICITY (Page 192)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4896', '', '19', '  a. Wire a buzzer or doorbell.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4897', '', '20', '  b. Make an electric buzzer game.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4898', '', '21', '  c. Make a simple bar or horseshoe electromagnet.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4899', '', '22', '  d. Use a simple electric motor.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4900', '', '23', '  e. Make a crane with an electromagnetic lift.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4901', '', '24', '5. BOATS (Page 196)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4902', '', '25', '  a. Help an adult rig and sail a real boat. (Wear your PFD.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4903', '', '26', '  b. Help an adult repair a real boat or canoe.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4904', '', '27', '  c. Know the flag signals for storm warnings.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4905', '', '28', '  d. Help an adult repair a boat dock.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4906', '', '29', '  e. With an adult on board, and both wearing PFDs, row a boat around a 100-yard course that has two turns. Demonstrate forward strokes, turns to both sides, and backstrokes.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4907', '', '30', '6. AIRCRAFT (Page 202)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4908', '', '31', '  a. Identify five different kinds of aircraft, in flight if possible, or from models or photos.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4909', '', '32', '  b. Ride in a commercial airplane.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4910', '', '33', '  c. Explain how a hot air balloon works.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4911', '', '34', '  d. Build and fly a model airplane. (You may use a kit. Every time you do this differently, it counts as a completed project.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4912', '', '35', '  e. Sketch and label an airplane showing the direction of forces acting on it (lift, drag, and load).', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4913', '', '36', '  f. Make a list of some of the things a helicopter can do that other kinds of airplanes can\'t. Draw or cut out a picture of a helicopter and label the parts.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4914', '', '37', '  g. Build and display a scale airplane model. You may use a kit or build it from plans.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4915', '', '38', '7. THINGS THAT GO (Page 206)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4916', '', '39', '  a. With an adult\'s help, make a scooter or a Cubmobile. Know the safety rules.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4917', '', '40', '  b. With an adult\'s help, make a windmill.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4918', '', '41', '  c. With an adult\'s help, make a waterwheel.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4919', '', '42', '  d. Make an invention of your own design that goes.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4920', '', '43', '8. CUB SCOUT BAND (Page 210)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4921', '', '44', '  a. Make and play a homemade musical instrument - cigar-box banjo, washtub bull fiddle, a drum or rhythm set, tambourine. etc.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4922', '', '45', '  b. Learn to play two familiar tunes on any musical instrument.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4923', '', '46', '  c. Play in a den band using homemade or regular musical instruments. Play at a pack meeting.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4924', '', '47', '  d. Play two tunes on any recognized band or orchestra instrument.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4925', '', '48', '9. ART (Page 214)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4926', '', '49', '  a. Do an original art project and show it at a pack meeting. Every project you do counts as one requirement\r\n   Here are some ideas for art projects:\r\n   Mobile or wire sculpture, Silhouette, Acrylic painting, Watercolor painting, Collage, Mosaic, Clay sculpture, Silk screen picture.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4927', '', '50', '  b. Visit an art museum or picture gallery with your den or family.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4928', '', '51', '  c. Find a favorite outdoor location and draw or paint it.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4929', '', '52', '10. MASKS (Page 218)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4930', '', '53', '  a. Make a simple papier-mache mask.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4931', '', '54', '  b. Make an animal mask.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4932', '', '55', '  c. Make a clown mask.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4933', '', '56', '11. PHOTOGRAPHY (Page 222)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4934', '', '57', '  a. Practice holding a camera still in one position. Learn to push the shutter button without moving the camera. Do this without film in the camera until you have learned how. Look through the viewfinder and see what your picture will look like. Make sure that everything you want in your picture is in the frame of your viewfinder.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4935', '', '58', '  b. Take five pictures of the same subject in different kinds of light.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4936', '', '59', '     -Subject in direct sun with direct light.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4937', '', '60', '     -Subject in direct sun with side light.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4938', '', '61', '     -Subject in direct sun with back light.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4939', '', '62', '     -Subject in shade on a sunny day.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4940', '', '63', '     -Subject on a cloudy day.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4941', '', '64', '  c. Put your pictures to use.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4942', '', '65', '     -Mount a picture on cardboard for display.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4943', '', '66', '     -Mount on cardboard and give it to a friend.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4944', '', '67', '     -Make three pictures that show how something happened (tell a story) and write a one sentence explanation for each.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4945', '', '68', '  d. Take a picture in your house.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4946', '', '69', '     -With available light.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4947', '', '70', '     -Using a flash attachment or photoflood (bright light).', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4948', '', '71', '12. NATURE CRAFTS (Page 226)\r\n   This elective is also part of the Cub Scout World Conservation Award.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4949', '', '72', '  a. Make solar prints of three kinds of leaves.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4950', '', '73', '  b. Make a display of eight different animal tracks with an eraser print.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4951', '', '74', '  c. Collect, press, and label ten kinds of leaves.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4952', '', '75', '  d. Build a waterscope and identify five types of water life.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4953', '', '76', '  e. Collect eight kinds of plant seeds and label them.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4954', '', '77', '  f. Collect, mount, and label ten kinds of rocks or minerals.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4955', '', '78', '  g. Collect, mount, and label five kinds of shells.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4956', '', '79', '  h. Build and use a bird caller', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4957', '', '80', '13. MAGIC (Page 230)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4958', '', '81', '  a. Learn and show three magic tricks.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4959', '', '82', '  b. With your den, put on a magic show for someone else.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4960', '', '83', '  c. Learn and show four puzzles.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4961', '', '84', '  d. Learn and show three rope tricks.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4962', '', '85', '14. LANDSCAPING (Page 236)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4963', '', '86', '  a. With an adult, help take care of your lawn or flower beds or help take care of the lawn or flower beds of a public building, school, or church. Seed bare spots. Get rid of weeds. Pick up litter. Agree ahead of time on what you will do.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4964', '', '87', '  b. Make a sketch of a landscape plan for the area right around your home. Talk it over with a parent or den leader. Show which trees, shrubs and flowers you could plant to make the area look better.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4965', '', '88', '  c. Take part in a project with your family, den, or pack to make your neighborhood or community more beautiful. These might be having a cleanup party, painting, cleaning and painting trash barrels, and removing weeds. (Each time you do this differently, it counts as a completed project.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4966', '', '89', '  d. Build a greenhouse and grow twenty plants from seed. You can use a package of garden seeds, or use beans, pumpkin seeds, or watermelon seeds.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4967', '', '90', '15. WATER AND SOIL CONSERVATION (Page 240)\r\n   This elective is also part of the Cub Scout World Conservation Award.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4968', '', '91', '  a. Dig a hole or find an excavation project and describe the different layers of soil you see and feel. (Do not enter an excavation area alone or without permission.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4969', '', '92', '  b. Explore three kinds of earth by conducting a soil experiment.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4970', '', '93', '  c. Visit a burned-out forest or prairie area, or a slide area, with your den or your family. Talk to a soil and water conservation officer or forest ranger about how the area will be planted and cared for so that it will grow to be the way it was before the fire or slide', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4971', '', '94', '  d. What is erosion? Find out the kinds of grasses, trees, or ground cover you should plant in your area to help limit erosion.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4972', '', '95', '  e. As a den, visit a lake, stream, river, or ocean (whichever is nearest where you live). Plan and do a den project to help clean up this important source of water. Name four kinds of water pollution.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4973', '', '96', '16. FARM ANIMALS (Page 244)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4974', '', '97', '  a. Take care of a farm animal. Decide with your parent the things you will do and how long you will do them.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4975', '', '98', '  b. Name and describe six kinds of farm animals and tell their common uses.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4976', '', '99', '  c. Read a book about farm animals and tell your den about it.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4977', '', '100', '  d. With your family or den, visit a livestock exhibit at a county or state fair.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4978', '', '101', '17. REPAIRS (Page 246)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4979', '', '102', '  a. With the help of an adult, fix an electric plug or appliance.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4980', '', '103', '  b. Use glue or epoxy to repair something.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4981', '', '104', '  c. Remove and clean a drain trap.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4982', '', '105', '  d. Refinish or repaint something.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4983', '', '106', '  e. Agree with an adult in your family on some repair job to be done and do it. (Each time you do this differently, it counts as a completed project.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4984', '', '107', '18. BACKYARD GYM (Page 250)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4985', '', '108', '  a. Build and use an outdoor gym with at least three items from this list.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4986', '', '109', '     -Balance board', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4987', '', '110', '     -Trapeze', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4988', '', '111', '     -Tire walk', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4989', '', '112', '     -Tire swing', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4990', '', '113', '     -Tetherball', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4991', '', '114', '     -Climbing rope', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4992', '', '115', '     -Running long jump area.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4993', '', '116', '  b. Build three outdoor toss games.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4994', '', '117', '  c. Plan an outdoor game or gym day with your den. (This can be part of a pack activity). Put your plans on paper.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4995', '', '118', '  d. Hold an open house for your backyard gym.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4996', '', '119', '19. SWIMMING (Page 254)\r\n   There is something about this elective that is different from any other. That is this rule: whenever you are working on the Swimming elective, you must have an adult with you who can swim.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4997', '', '120', '  a. Jump feetfirst into water over your head, swim 25 feet on the surface, stop, turn sharply, and swim back.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4998', '', '121', '  b. Swim on your back, the elementary backstroke, for 30 feet.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('4999', '', '122', '  c. Rest by floating on your back, using as little motion as possible for at least one minute.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5000', '', '123', '  d. Tell what is meant by the buddy system. Know the basic rules of safe swimming', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5001', '', '124', '  e. Do a racing dive from edge of pool and swim 60 feet, using a racing stroke. (You might need to make a turn.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5002', '', '125', '20. SPORTS (Page 260)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5003', '', '126', '  a. In archery, know the safety rules and how to shoot correctly. Put six arrows into a 4-foot target at a distance of 15 feet. Make an arrow holder. (This can be done only at a district/council day or resident or family camp.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5004', '', '127', '  b. In skiing, know the Skier\'s Safety and Courtesy Code. Demonstrate walking and kick turn, climbing with a side step or herringbone, a snowplow stop, a stem turn, four linked snowplow or stem turns, straight running in a downhill position or cross-country position, and how to recover from a fall.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5005', '', '128', '  c. In ice skating, know the safety rules. From a standing start, skate forward 150 feet; and come to a complete stop within 20 feet. Skate around a corner clockwise and counterclockwise without coasting. Show a turn from forward to backward. Skate backward 50 feet.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5006', '', '129', '  d. In track, show how to make a sprint start. Run the 50-yard dash in 10 seconds or less. Show how to do the standing long jump, the running long jump, or high jump. (Be sure to have a soft landing area.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5007', '', '130', '  e. In roller skating (with conventional or in-line skates), know the safety rules. From a standing start, skate forward 150 feet; and come to a complete stop within 20 feet. Skate around a corner clockwise and counterclockwise without coasting and show a turn from forward to backward. Skate backward 50 feet. Wear the proper protective clothing.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5008', '', '131', '  f. Earn a new Cub Scout Sports pin. (Repeat three times with different sports to earn up to three Arrow Points.)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5009', '', '132', '21. SALES (Page 266)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5010', '', '133', '  a. Take part in a council- or pack-sponsored, money-earning sales program. Keep track of the sales you make yourself. When the program is over, add up the sales you have made.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5011', '', '134', '  b. Help with a garage sale or rummage sale. This can be with your family or a neighbor, or it can be a church, school, or pack event.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5012', '', '135', '22. COLLECTING THINGS (Page 268)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5013', '', '136', '  a. Start a stamp collection. You can get information about stamp collecting at any U.S. post office.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5014', '', '137', '  b. Mount and display a collection of emblems, coins, or other items to show at a pack meeting. This can be any kind of collection. Every time you show a different kind of collection, it counts as one requirement.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5015', '', '138', '  c. Start your own library. Keep your own books and pamphlets in order by subject. List the title, author, and subject of each on an index card and keep the cards in a file box, or use a computer program to store the information.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5016', '', '139', '23. MAPS (Page 270)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5017', '', '140', '  a. Look up your state on a U.S. map. What other states touch its borders?', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5018', '', '141', '  b. Find your city or town on a map of your state. How far do you live from the state capital?', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5019', '', '142', '  c. In which time zone do you live? How many time zones are there in the U.S.?', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5020', '', '143', '  d. Make a map showing the route from your home to your school or den meeting place.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5021', '', '144', '  e. Mark a map showing the way to a place you would like to visit that is at least 50 miles from your home.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5022', '', '145', '24. AMERICAN INDIAN LIFE (Page 272)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5023', '', '146', '  a. American Indian people live in every part of what is now the continental United States. Find the name of the American Indian nation that lives or has lived where you live now. Learn about these people.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5024', '', '147', '  b. Learn, make equipment for, and play two American Indian or other native American games with members of your den. Be able to tell the rules, who won, and what the score was.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5025', '', '148', '  c. Learn what the American Indian people in your area (or another area) used for shelter before contact with the Europeans. Learn what American Indian people in that area used for shelter today. Make a model of one of these shelters, historic or modern. Compare the kind of shelter you made with the others made in your den.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5026', '', '149', '25. Let\'s Go Camping (Page 276)', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5027', '', '150', '  a. Learn about the ten essential items you need for a hike or campout. Assemble your own kit of essential items. Explain why each item is \'essential.\'', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5028', '', '151', '  b. Go on a short hike with your den, following the buddy system. Explain how the buddy system works and why it is important to you to follow it. Tell what to do if you are lost.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5029', '', '152', '  c. Participate with your den in front of the pack at a campfire.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5030', '', '153', '  d. Participate with your pack on an overnight campout. Help put up your tent and hlp set up the campsite.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5031', '', '154', '  e. Participate with your den in a religious service during an overnight campout or other Cub Scouting event.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5032', '', '155', '  f. Attend day camp in your area.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5033', '', '156', '  g. attend resident camp in your area.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5034', '', '157', '  h. Earn the Cub Scout Leave No Trace Award.', '506', '');
INSERT INTO `RequirementConfig` VALUES ('5035', '', '1', 'Do all of the following:', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5036', '', '2', 'As with all Cub Scout ranks, you must first earn the Bobcat Badge to be eligible to work for this award.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5037', '', '3', '1. Have an adult member of your family read the Webelos Scout Parent Guide that comes with this book (pages 1-22) and sign here.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5038', '', '4', '2. Be an active member of your Webelos den for 3 months (Active means having good attendance, paying den dues, working on den projects).', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5039', '', '5', '3. Know and explain the meaning of the Webelos badge.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5040', '', '6', '4. Point out the three special parts of the Webelos Scout uniform. Tell when to wear the uniform and when not to wear it.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5041', '', '7', '5. Earn the Fitness and Citizen activity badges and one other activity badge from a different activity group .', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5042', '', '8', '6. Plan and lead a flag ceremony in your den that includes the U.S. flag.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5043', '', '9', '7. Show that you know and understand the requirements to be a Boy Scout.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5044', '', '10', '    a. Demonstrate the Scout salute, Scout sign, and Scout handshake. Explain when you would use them.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5045', '', '11', '    b. Explain the Scout Oath, Scout Law, Scout motto, and Scout slogan', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5046', '', '12', '    c. Explain and agree to follow the Outdoor Code.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5047', '', '13', '8. Faith - After completing the rest of requirement 8, do these (a, b, and c):', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5048', '', '14', '    a. Know: Tell what you have learned about faith.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5049', '', '15', '    b. Commit: Tell how these faith experiences help you live your duty to God. Name one faith practice that you will continue to do in the future.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5050', '', '16', '    c. Practice: After doing these requirements, tell what you have learned about your beliefs.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5051', '', '17', 'Do one of the following (d OR e):', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5052', '', '18', '    d. Earn the religious emblem of your faith*', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5053', '', '19', '    e. Do two of these:', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5054', '', '20', '      -Attend the mosque, church, synagogue, temple, or other religious organization of your choice, talk with your religious leader about your beliefs. Tell your family and your Webelos den leader what you learned.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5055', '', '21', '      -Discuss with your family and Webelos den leader how your religious beliefs fit in with the Scout Oath and Scout Law, and what character-building traits your religious beliefs have in common with the Scout Oath and Scout Law.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5056', '', '22', '      -With your religious leader, discuss and make a plan to do two things you think will help you draw nearer to God. Do these things  for a month.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5057', '', '23', '      -For at least a month, pray or meditate reverently each day as taught by your family, and by your church, temple, mosque, synagogue, or religious group.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5058', '', '24', '      -Under the direction of your religious leader, do an act of service for someone else. Talk about your service with your family and Webelos den leader. Tell them how it made you feel.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5059', '', '25', '      -List at least two ways you believe you have lived according to your religious beliefs.', '507', '');
INSERT INTO `RequirementConfig` VALUES ('5060', '', '1', '1. Be active in your Webelos den for at least six months since completing the fourth grade (or for at least six months since becoming 10 years old), and earn the Webelos badge.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5061', '', '2', '2. Show your knowledge of the requirements to become a Boy Scout by doing all of these:', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5062', '', '3', '  -Repeat from memory and explain in your own words the Scout Oath or Promise and the 12 points of the Scout Law. Tell how you have practiced them in your everyday life.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5063', '', '4', '  -Give and explain the Scout motto, slogan, sign, salute, and handshake.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5064', '', '5', '  -Understand the significance of the First Class Scout badge. Describe its parts and tell what each stands for.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5065', '', '6', '  -Tell how a Boy Scout uniform is different from a Webelos Scout uniform.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5066', '', '7', '  -Tie the joining knot (square knot)', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5067', '', '8', '3. Earn five more activity badges in addition to the three you already earned for the Webelos badge. These must include:', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5068', '', '9', '  -Fitness (already earned for the Webelos badge)', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5069', '', '10', '  -Citizen (already earned for the Webelos badge)', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5070', '', '11', '  -Readyman', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5071', '', '12', '  -Outdoorsman', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5072', '', '13', '  -At least one from the Mental Skills Group', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5073', '', '14', '  -At least one from the Technology Group', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5074', '', '15', '  -One more of your choice.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5075', '', '16', '  -(Plus the one other badge of your choice that was already earned for the Webelos badge)', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5076', '', '17', '4. With your Webelos den, visit at least', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5077', '', '18', '  -one Boy Scout troop meeting, and', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5078', '', '19', '  -one Boy Scout-oriented outdoor activity. (If you have already done this when you earned your Outdoorsman activity badge, you may not use the same outing to fulfill requirements for your Arrow of Light Award requirements.)', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5079', '', '20', '5. Participate in a Webelos overnight campout or day hike. (If you have already done this when you earned your Outdoorsman activity badge, you may not use the same outing to fulfill requirements for your Arrow of Light Award requirements.)', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5080', '', '21', '6. After you have completed all five of the above requirements, and after a talk with your Webelos den leader, arrange to visit, with your parent or guardian, a meeting of a Boy Scout troop you think you might like to join. Have a conference with the Scoutmaster.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5081', '', '22', '7. Complete the Honesty Character Connection.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5082', '', '23', '  a. Know: Say the Cub Scout Promise to your family. Discuss these questions with them. What is a promise? What does it mean to keep your word? What does it mean to be trustworthy? What does honesty mean?', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5083', '', '24', '  b. Commit: Discuss these questions with your family. Why is a promise important? Why is it important for people to trust you when you give your word? When might it be difficult to be truthful? List examples.', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5084', '', '25', '  c. Practice: Discuss with a family member why it is important to be trustworthy and honest. How can you do your best to be honest even when it is difficult?', '508', '');
INSERT INTO `RequirementConfig` VALUES ('5085', '', '1', 'Do all three:', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5086', '', '2', '1. Jump into water over your head. Come to the surface and swim 100 feet, at least half of this using a backstroke.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5087', '', '3', '2. Stay in the water after the swim and float on your back and your front, and demonstrate survival floating.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5088', '', '4', '3. Put on a personal floatation device (PFD) that is the right size for you. Make sure it is properly fastened. Wearing the PFD, jump into water over your head. Show how the PFD keeps your head above water by swimming 25 feet. Get out of the water, remove the PFD, and hang it where it will dry.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5089', '', '5', 'Do three of the following:', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5090', '', '6', '4. Do a front surface dive and swim under water for four strokes before returning to the surface.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5091', '', '7', '5. Explain the four basic water rescue methods. Demonstrate reaching and throwing rescues.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5092', '', '8', '6. With an adult on board, show that you know how to handle a rowboat.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5093', '', '9', '7. Pass the BSA \'Swimmer\' test:', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5094', '', '10', '    - Jump feet-first into water over the head in depth, level off, and begin swimming.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5095', '', '11', '    - Swim 75 yards in a strong manner using one or more of the following strokes: sidestroke, breaststroke, trudgen, or crawl.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5096', '', '12', '    - Then swim 25 yards using an easy, resting backstroke.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5097', '', '13', '    - After completing the swim, rest by floating.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5098', '', '14', '8. While you are a Webelos Scout, earn the Cub Scout Sports belt loop for swimming.', '510', '');
INSERT INTO `RequirementConfig` VALUES ('5099', '', '1', 'Do all five of the following:', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5100', '', '2', '1. With your parent, guardian, or Webelos den leader, complete the Perseverance Character Connection.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5101', '', '3', '    a. Know: Review the requirements and decide which ones might be more difficult for you to do. Make a plan to complete one of the harder requirements.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5102', '', '4', '    b. Commit: When doing the harder requirement, did you ever feel frustrated or angry? What did perseverance have to do with that? Name another type of task for which you will need to persevere.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5103', '', '5', '    c. Practice: Practice perseverance by following your plan to do that requirement for the Athlete activity badge.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5104', '', '6', '2. Explain what it means to be physically and mentally healthy.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5105', '', '7', '3. Explain what you as a Webelos Scout can do to stay physically and mentally healthy.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5106', '', '8', '4. Every time you work on requirement 5 below, start with at least 5 minutes of stretching warm-up activities.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5107', '', '9', '5. Do as many as you can of the following and record your results. Show improvement in all of the activities after 30 days.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5108', '', '10', '    a. Have another person hold your feet down while you do as many curl-ups as you can.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5109', '', '11', '    b. Do as many pull-ups from a bar as you can.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5110', '', '12', '    c. Do as many push-ups from the ground or floor as you can.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5111', '', '13', '    d. Do a standing long jump as far as you can.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5112', '', '14', '    e. Do a quarter-mile run or walk.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5113', '', '15', 'Do two of the following:', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5114', '', '16', '6. Do a vertical jump and improve your reach in 30 days.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5115', '', '17', '7. Do a 50 yard dash as fast as you can, and show a decrease in time over a 30 day period.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5116', '', '18', '8. Ride a bike 1 mile as fast as you can, and show a decrease in time over a 30 day period.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5117', '', '19', '9. Swim a quarter mile in a pool or lake as fast as you can, and show a decrease in time over a 30 day period.', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5118', '', '20', '10. While you are a Webelos Scout, earn the Cub Scout Sports Pin for physical fitness.\r\n    NOTE: Requirement 10 above does NOT appear in the list of requirements on page 125 of the Webelos Handbook, but is referenced on the following page. Also note that for requirement 10, you must earn the Physical Fitness Sports Pin while you are a Weblos Scout. (even if you earned it while in a Cub Scout Den).', '511', '');
INSERT INTO `RequirementConfig` VALUES ('5119', '', '1', 'Do all:', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5120', '', '2', '1. With your parent, guardian, or Webelos den leader, complete the Health and Fitness Character Connection.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5121', '', '3', '    a. Know: Tell why it is important to be healthy, clean, and fit.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5122', '', '4', '    b. Commit: Tell when it is difficult for you to stick with good health habits. Tell where you can go to be with others who encourage you to be healthy, clean, and fit.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5123', '', '5', '    c. Practice: Practice good health habits while doing the requirements for this activity badge.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5124', '', '6', 'Do six of the following:', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5125', '', '7', '2. With a parent or other adult family member complete a safety notebook, which is discussed in the booklet \'How to Protect Your Children from Child Abuse\' that comes with this book.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5126', '', '8', '3. Read the meal planning information in this chapter. With a parent or other family member, plan a week of meals. Explain what kinds of meals are best for you and why.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5127', '', '9', '4. Keep a record of your daily meals and snacks for a week. Decide whether you have been eating foods that are good for you.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5128', '', '10', '5. Tell an adult member of your family about the bad effects smoking or chewing tobacco would have on your body.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5129', '', '11', '6. Tell an adult member of your family four reasons why you should not use alcohol and how it could affect you.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5130', '', '12', '7. Tell an adult member of your family what drugs could do to your body and how they would affect your ability to think clearly.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5131', '', '13', '8. Read the booklet Choose to Refuse! Discuss it with an adult and show that you understand the material.', '512', '');
INSERT INTO `RequirementConfig` VALUES ('5132', '', '1', 'Do all:', '513', '');
INSERT INTO `RequirementConfig` VALUES ('5133', '', '2', '1. Show the signals used by officials in one of these sports: football, basketball, baseball, soccer, or hockey.', '513', '');
INSERT INTO `RequirementConfig` VALUES ('5134', '', '3', '2. Explain what good sportsmanship means.', '513', '');
INSERT INTO `RequirementConfig` VALUES ('5135', '', '4', '3. While you are a Webelos Scout, earn Cub Scouting Sports belt loops for two individual sports (badminton, bicycling, bowling, fishing, golf, gymnastics, marbles, physical fitness, ice skating, roller skating, snow ski and board sports, swimming, table tennis, or tennis).', '513', '');
INSERT INTO `RequirementConfig` VALUES ('5136', '', '5', '4. While you are a Webelos Scout, earn Cub Scouting Sports belt loops for two team sports (baseball, basketball, soccer, softball, volleyball, flag football, or ultimate).', '513', '');
INSERT INTO `RequirementConfig` VALUES ('5137', '', '1', 'Do all of the following:', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5138', '', '2', '1. Talk to an artist in your area or to your art teacher about the different occupations in the art field. Make a list of them.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5139', '', '3', '2. Create a scrapbook (portfolio) of your Artist activity badge projects and show it to your den leader.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5140', '', '4', 'Do five of the following:', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5141', '', '5', '3. Draw or paint an original picture out-of-doors, using the art materials you prefer. Frame the picture for your room or home.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5142', '', '6', '4. List the primary and secondary colors. Explain what happens when you combine colors.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5143', '', '7', '5. Using a computer, make six original designs using straight lines, curved lines, or both.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5144', '', '8', '6. Draw a profile of a member of your family.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5145', '', '9', '7. Use clay to sculpt a simple object.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5146', '', '10', '8. Make a mobile, using your choice of materials.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5147', '', '11', '9. Make an art construction, using your choice of materials.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5148', '', '12', '10. Create a collage that expresses something about you.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5149', '', '13', '11. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Art.', '514', '');
INSERT INTO `RequirementConfig` VALUES ('5150', '', '1', 'Do the following::', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5151', '', '2', '1. With your parent, guardian, or Webelos den leader, complete the Positive Attitude Character Connection.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5152', '', '3', '    a. Know: Discuss with your parent, guardian, or your Webelos den leader, what it means to have a positive attitude and the \'BEST\' steps you can take to have a positive attitude. (Believe it can happen, Expect success, Set your mind, and Try, try, try.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5153', '', '4', '    b. Commit: Plan with your parent, guardian, or your Webelos den leader, how you will apply the \'BEST\' steps for a positive attitude in doing your school-work and in other areas of your life.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5154', '', '5', '    c. Practice: Do your \'BEST\' to have a cheerful and positive attitude while doing the requirements for this activity badge.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5155', '', '6', 'Do three of the following:', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5156', '', '7', '2. Have a good record in attendance, behavior, and grades at school.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5157', '', '8', '3. Take an active part in a school activity or service.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5158', '', '9', '4. Discuss with your teacher or principal the value of having an education.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5159', '', '10', '5. List in writing some important things you can do now because of what you\'ve learned in school.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5160', '', '11', '6. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Language.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5161', '', '12', '7. While you are a Webelos Scout, and if you have not earned it for another activity badge, earn the Cub Scout Academics belt loop for Mathematics.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5162', '', '13', '8. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Chess.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5163', '', '14', 'Do three of the following:', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5164', '', '15', '9. Trace through history the different kinds of schools. Tell how our present public school system grew out of these early schools.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5165', '', '16', '10. Make a chart showing how your school system is run.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5166', '', '17', '11. Ask a parent and five other grown-ups these questions:', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5167', '', '18', '    -What do you think are the best things about my school?', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5168', '', '19', '    -What are its main problems?', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5169', '', '20', '    -Tell what you think were the best answers and why?', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5170', '', '21', '12. List and explain some of the full-time positions in the field of education.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5171', '', '22', '13. Help another student with schoolwork. Tell what you did to help.', '515', '');
INSERT INTO `RequirementConfig` VALUES ('5172', '', '1', 'Do the following:', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5173', '', '2', '1. Complete six activities of your choice; these can be from any area (puppetry, music, or drama.)\r\n   For requirement 1 of the Showman activity badge, you\'ll choose six activities from puppetry, music, or drama. Then later you\'ll do one additional activity in each of these areas, for a total of 9 activities.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5174', '', '3', 'PUPPETRY\r\nAnd do one of these not already done for requirement 1:', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5175', '', '4', '2. Write a puppet play about one of your Webelos den activities or a subject of your choice.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5176', '', '5', '3. Make a set of puppets or marionettes for the play you have written or for another play.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5177', '', '6', '4. Build a simple stage for marionettes or puppets.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5178', '', '7', '5. Alone or with the help of others, put on a puppet show for your den or pack.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5179', '', '8', '6. Make a set of four paper bag puppets for a singing group. With the help of three other den members, sing a song with the puppets as the performers.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5180', '', '9', '7. There are sock, stick and finger puppets. There are paper bag puppets and marionettes. Explain their differences and show any puppets you have made for this badge.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5181', '', '10', 'MUSIC\r\nAnd do one of these not already done for requirement 1:', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5182', '', '11', '8. Play four tunes on any band or orchestra instrument. Read these from music.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5183', '', '12', '9. Sing one song indoors and one song outdoors, either alone or with a group. Tell what you need to do differently when singing outdoors.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5184', '', '13', '10. Make a collection of three or more records, tapes, or music CDs. Tell what you like about each one.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5185', '', '14', '11. Tell what folk music is. Hum, sing, or play a folk tune on a musical instrument.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5186', '', '15', '12. Name three American composers. Name a famous work by each.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5187', '', '16', '13. Draw a staff. Draw on it a clef, sharp, flat, natural, note, and rest. Tell what each is used for.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5188', '', '17', '14. Show the difference between 2/4, 3/4, and 4/4 time by beating time or playing an instrument.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5189', '', '18', '15. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Music.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5190', '', '19', 'DRAMA\r\nAnd do one of these not already done for requirement 1:', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5191', '', '20', '16. Give a monologue (a talk) on a patriotic, humorous, or holiday subject, or another subject of your choice.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5192', '', '21', '17. Attend a play. Describe the story. Tell what you liked about it.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5193', '', '22', '18. Read a play. Make a model stage setting for one of the acts.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5194', '', '23', '19. Write, put on, and take part in a one-act play.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5195', '', '24', '20. Make a list of stage directions. Tell what they mean.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5196', '', '25', '21. Describe a theater-in-the-round. What are its good and bad points?', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5197', '', '26', '22. Explain the difference between a grand opera and a light opera.  Explain the difference between a musical and a play.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5198', '', '27', '23. Read about William Shakespeare. Draw a picture of his Globe Theater.', '516', '');
INSERT INTO `RequirementConfig` VALUES ('5199', '', '1', 'Do five of the following:', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5200', '', '2', '1. Get a map or timetable from a railroad, bus line, airline, subway, or light rail. The line should serve the place where you live or near where you live. Look up some places it goes.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5201', '', '3', '2. Use a timetable to plan a trip from your home to a city in another state by railroad, bus, airline, or ferry.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5202', '', '4', '3. With the help of your parent, guardian, teacher, or librarian, use a map site on the Internet to plan a trip from your home to a nearby place of interest. Download and/or print the directions and street map showing how to go from your home to the place you chose.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5203', '', '5', '4. With your parent or guardian, take a trip to a place that interests you. Go by car, bus, boat, train, or plane.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5204', '', '6', '5. Figure out what it costs per mile for the trip you took or planned to fulfill requirement 2, 4, 6, or 7. (Don\'t forget to include getting back to your starting point.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5205', '', '7', '6. Decide on four nearby trips you would like to take with your parents or guardian. Draw the route of each trip on a highway map. Using the map, act as navigator on one of these trips. It should start at your home, be at least 25 miles long, and have six or more turns.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5206', '', '8', '7. Decide on a trip you would like to take that lasts at least two days. Pack everything you would need for that trip.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5207', '', '9', '8. Check the first aid kit in the family car to see if it contains what is needed. Explain what you found.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5208', '', '10', '9. Look at a map legend on a road map of your area. Learn what the symbols mean. Show your den members what you have learned.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5209', '', '11', '10. On a road map of your area, find a place of interest, and draw two different routes between it and your home. Use the map legend to determine which route is shorter in miles.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5210', '', '12', '11. Make a list of safety precautions you, as a traveler, should take for travel by each of the following; car, bus, plane, boat, train.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5211', '', '13', '12. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Geography.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5212', '', '14', '13. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Map and Compass.', '517', '');
INSERT INTO `RequirementConfig` VALUES ('5213', '', '1', 'Do the following:', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5214', '', '2', '1. With your parent, guardian, or Webelos den leader, complete the Citizenship Character Connection.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5215', '', '3', '    a. Know: List some of your rights as a citizen of the United States of America. Tell ways you can show respect for the rights of others.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5216', '', '4', '    b. Commit: Name some ways a boy your age can be a good citizen. Tell how you plan to be a good citizen and how you plan to influence others to be good citizens.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5217', '', '5', '    c. Practice: Choose one of the requirements for this activity badge that helps you be a good citizen. Complete the requirement and tell why completing it helped you be a good citizen.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5218', '', '6', 'Do all of the following:', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5219', '', '7', '2. Know the names of the President and Vice-President of the United States, elected Governor of your state and the head of your local government.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5220', '', '8', '3. Describe the flag of the United States and give a short history of it. With another Webelos Scout helping you, show how to hoist and lower the flag, how to hang it horizontally and vertically on a wall, and how to fold it. Tell how to retire a worn or tattered flag properly.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5221', '', '9', '4. Explain why you should respect your country\'s flag. Tell some of the special days we fly it. Tell when to salute the flag and show how to do it.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5222', '', '10', '5. Repeat the Pledge of Allegiance from memory. Explain its meaning in your own words.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5223', '', '11', '6. Tell how our National Anthem was written.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5224', '', '12', '7. Explain the rights and duties of a citizen of the United States. Explain what a citizen should do to save our natural resources.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5225', '', '13', '8. As a Webelos Scout, earn the Cub Scout Academics belt loop for Citizenship (if not already earned) or earn the pin. At a Webelos den meeting, talk about the service project Good Turn that you did.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5226', '', '14', 'Do two of the following:', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5227', '', '15', '9. Tell about two things you can do that will help law enforcement agencies.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5228', '', '16', '10. With your Webelos den or your family, visit a community leader. Learn about the duties of the job or office and tell what you have learned.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5229', '', '17', '11. Write a short story of not less than 50 words about a former U.S. president or some other great American. Give a report on this to your Webelos den.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5230', '', '18', '12. Tell about another boy you think is a good citizen. Tell what he does that makes you think he is a good citizen.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5231', '', '19', '13. List the names of three people you think are good citizens. (They can be from any country.) Tell why you chose each of them.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5232', '', '20', '14. Tell why we have laws. Tell why you think it is important to obey the law. Tell about three laws you obeyed this week.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5233', '', '21', '15. Tell why we have government. Explain some ways your family helps pay for government.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5234', '', '22', '16. List four ways in which your country helps or works with other nations.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5235', '', '23', '17. Name three organizations, not churches or other religious organizations, in your area that help people. Tell something about what one of these organizations does.', '518', '');
INSERT INTO `RequirementConfig` VALUES ('5236', '', '1', 'Do seven of the following:', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5237', '', '2', '1. Play the Body Language Game with your den.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5238', '', '3', '2. Prepare and give a three-minute talk to your den on a subject of your choice.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5239', '', '4', '3. Invent a sign language or a picture writing language and use it to tell someone a story.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5240', '', '5', '4. Identify and discuss with your den as many different methods of communication as you can (at least six different methods).', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5241', '', '6', '5. Invent your own den secret code and send one of your den members a secret message.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5242', '', '7', '6. With your den or your family, visit a library and talk to a librarian. Learn how books are catalogued to make them easy to find. Sign up for a library card , if you don\'t already have one.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5243', '', '8', '7. Visit the newsroom of a newspaper or a radio or television station and find out how they receive information.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5244', '', '9', '8. Write an article about a den activity for your pack newsletter or web site, your local newspaper, or your school newsletter, newspaper, or Web site.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5245', '', '10', '9. Invite a person with a visual, speaking, or hearing impairment to visit your den. Ask about the special ways he or she communicates. Discover how well you can communicate with him or her.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5246', '', '11', '10. With your parent or guardian, or your Webelos den leader, invite a person who speaks another language (such as Spanish, French, Arabic, Hebrew, etc.) as well as English to visit your den. Ask questions about the other language (its background, where it is spoken, etc.), discuss words in that language that den members are already familiar with, or ask about ways to learn another language.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5247', '', '12', '11. Use a personal computer to write a letter to a friend or relative. Create your letter, check it for grammar and spelling, and save it to a disk. Print it.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5248', '', '13', '12. Search the Internet and connect to five Web sites that interest you.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5249', '', '14', '13. Under the supervision of a parent or other trusted adult, exchange e-mail with a friend or relative.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5250', '', '15', '14. While you are a Webelos Scout, earn the Academics belt loop for Computers.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5251', '', '16', '15. While you are a Webelos Scout, earn the Academics belt loop for Communicating.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5252', '', '17', '16. Find out about jobs in communications. Tell your den what you learn.', '519', '');
INSERT INTO `RequirementConfig` VALUES ('5253', '', '1', 'Do all of the following:', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5254', '', '2', '1. Tell what is meant by family, duty to family, and family meetings.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5255', '', '3', '2. Make a chart showing the jobs you and other family members have at home. Talk with your family about other jobs you can do for the next two months.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5256', '', '4', '3. Make a list of some things for which your family spends money. Tell how you can help your family save money.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5257', '', '5', '4. Plan your own budget for 30 days. Keep track of your daily expenses for seven days.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5258', '', '6', '5. Take part in at least four family meetings and help make decisions. The meetings might involve plans for family activities, or they might be about serious topics that your parent wants you to know about.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5259', '', '7', '6. With the help of an adult inspect your home and surroundings. Make a list of hazards or lack of security that you find. Correct one problem that you found and tell what you did.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5260', '', '8', 'Do two of the following:', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5261', '', '9', '7. With the help of an adult prepare a family energy-saving plan. Explain what you did to carry it out.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5262', '', '10', '8. Tell what your family does for fun. Make a list of fun things your family might do for little or no cost. Plan a family fun night.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5263', '', '11', '9. Learn how to clean your home properly. With adult supervision, help do it for one month.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5264', '', '12', '10. Show that you know how to take care of your clothes. With adult supervision, help at least twice with the family laundry.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5265', '', '13', '11. With adult supervision, help plan the meals for your family for one week. Help buy the food and help prepare three meals for your family.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5266', '', '14', '12. While you are a Webelos Scout, earn the Academics belt loop for Heritages.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5267', '', '15', '13. Explain why garbage and trash must be disposed of properly.', '520', '');
INSERT INTO `RequirementConfig` VALUES ('5268', '', '1', 'Do all of the following:', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5269', '', '2', '1. With your parent, guardian, or Webelos den leader, complete the Courage Character Connection.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5270', '', '3', '    a. Know: Define the importance of each courage step: Be strong; Be calm; Be clear; Be careful. Explain how memorizing the courage steps helps you to be ready.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5271', '', '4', '    b. Commit: Explain why it is hard to follow the courage steps in an emergency. Tell when you can use the courage steps in other situations (such as standing up to a bully, avoiding fights, being fair, not stealing or cheating when tempted, etc.)', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5272', '', '5', '    c. Practice: Act out one of the requirements using these courage steps: Be strong; Be calm; Be clear; Be careful.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5273', '', '6', '2. Explain what first aid is. Tell what you should do after an accident.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5274', '', '7', '3. Explain how you can get help quickly if there is an emergency in your home. Make a \'help list\' of people or agencies that can help you if you need it. Post it near a phone or another place with easy access.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5275', '', '8', '4. Demonstrate the Heimlich maneuver and tell when it is used.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5276', '', '9', '5. Show what to do for these \'hurry cases\':', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5277', '', '10', '    -Serious bleeding', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5278', '', '11', '    -Stopped breathing', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5279', '', '12', '    -Internal poisoning', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5280', '', '13', '    -Heart attack', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5281', '', '14', '6. Show how to treat shock.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5282', '', '15', '7. Show first aid for the following:', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5283', '', '16', '    -Cuts and scratches', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5284', '', '17', '    -Burns and scalds', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5285', '', '18', '    -Choking', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5286', '', '19', '    -Blisters on the hand and foot', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5287', '', '20', '    -Tick bites', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5288', '', '21', '    -Bites and stings of insects other than ticks', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5289', '', '22', '    -Poisonous snakebite', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5290', '', '23', '    -Nosebleed', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5291', '', '24', '    -Frostbite', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5292', '', '25', '    -Sunburn', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5293', '', '26', '8. Tell what steps must be taken for a safe swim with your Webelos den, pack, family, or other group. Explain the reasons for the buddy system.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5294', '', '27', 'And do two of the following:', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5295', '', '28', '9. Explain six safety rules you should follow when \'driving\' a bicycle.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5296', '', '29', '10. Explain the importance of wearing safety equipment when participating in sports activities (skating, skateboarding, etc.)', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5297', '', '30', '11. Make a home fire escape plan for your family.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5298', '', '31', '12. Explain how to use each item in a first aid kit.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5299', '', '32', '13. Tell where accidents are most likely to happen inside and around your home.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5300', '', '33', '14. Explain six safety rules you should remember when riding in a car.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5301', '', '34', '15. Attend a first aid demonstration at a Boy Scout troop meeting, a Red Cross center, or other community event or place.', '521', '');
INSERT INTO `RequirementConfig` VALUES ('5302', '', '1', 'Do all of the following:', '522', '');
INSERT INTO `RequirementConfig` VALUES ('5303', '', '2', '1. Explain how to safely handle the tools that you will use for this activity badge safely.', '522', '');
INSERT INTO `RequirementConfig` VALUES ('5304', '', '3', '2. With adult supervision and using hand tools, construct two different wooden objects you and your Webelos den leader agree on, such as the items listed in the book. Use a coping saw or jigsaw for these projects. Put them together with glue, nails, or screws. Paint or stain them.', '522', '');
INSERT INTO `RequirementConfig` VALUES ('5305', '', '4', '3. Make a display stand, or box to be used to display a model or an award. Or make a frame for a photo or painting. Use suitable material.', '522', '');
INSERT INTO `RequirementConfig` VALUES ('5306', '', '5', '4. Make four useful items using materials other than wood that you and your Webelos den leader agree on, such as clay, plastic, leather, metal, paper, rubber, or rope. These should be challenging items and must involve several operations.', '522', '');
INSERT INTO `RequirementConfig` VALUES ('5307', '', '1', 'Do both of the following:', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5308', '', '2', '1. Talk to an engineer, surveyor, or architect in your area about the different occupations in engineering. Create a list that tells what they do', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5309', '', '3', '2. Draw a floor plan of your house. Include doors, windows, and stairways.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5310', '', '4', 'Do four of the following:', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5311', '', '5', '3. Visit a construction job. Look at a set of plans used to build the facility or product. Tell your Webelos den leader about these. (Get permission before you visit.)', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5312', '', '6', '4. Visit a civil engineer or surveyor to learn how to measure the length of a property line. Explain how property lines are determined.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5313', '', '7', '5. Tell about how electricity is generated and then gets to your home.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5314', '', '8', '6. Construct a simple working electrical circuit using a flashlight battery, a switch, and a light.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5315', '', '9', '7. Make drawings of three kinds of bridges and explain their differences. Construct a model bridge of your choice.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5316', '', '10', '8. Make a simple crane using a block and tackle and explain how the block and tackle is used in everyday life.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5317', '', '11', '9. Build a catapult and show how it works.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5318', '', '12', '10. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Mathematics.', '523', '');
INSERT INTO `RequirementConfig` VALUES ('5319', '', '1', 'Do these', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5320', '', '2', '1. With your parent, guardian, or Webelos den leader, complete the Responsibility Character Connection.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5321', '', '3', '    a. Know: List all the tasks you can think of that are necessary in keeping a household in good shape. Name the tasks that are your responsibility. Tell what it means to be responsible for these tasks.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5322', '', '4', '    b. Commit: Talk about what happens when people don\'t do their jobs. Tell why it is important to be helpful and to be responsible. list ways that you can be more responsible on your own.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5323', '', '5', '    c. Practice: Choose one of the requirements and show how you are responsible by doing that task well for two weeks.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5324', '', '6', 'Do six of the following:', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5325', '', '8', '2. With adult supervision, wash a car.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5326', '', '8', '3. Help an adult change a tire on a car.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5327', '', '9', '4. With adult supervision, replace a bulb in the taillight, turn signal, or parking light or replace a headlight on a car.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5328', '', '10', '5. With adult supervision, show how to check the oil level and tire pressure on a car.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5329', '', '11', '6. Make a repair to a bicycle, such as tightening the chain, fixing a flat tire, or adjusting the seat or handlebars.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5330', '', '12', '7. Properly lubricate the chain and crank on a bicycle.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5331', '', '13', '8. Properly inflate the tires on a bicycle.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5332', '', '14', '9. Change the wheels on a skateboard or pair of inline skates.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5333', '', '15', '10. Replace a light bulb in a fixture or lamp.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5334', '', '16', '11. With adult supervision, arrange a storage area for household cleaners and other dangerous materials where small children cannot reach them.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5335', '', '17', '12. Build a sawhorse or stool to be used around your home.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5336', '', '18', '13. Help take care of the lawn.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5337', '', '19', '14. Arrange a storage area for hand tools or lawn and garden tools.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5338', '', '20', '15. Clean and properly store hand tools or lawn and garden tools in their storage area.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5339', '', '21', '16. Label hand tools or lawn and garden tools.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5340', '', '22', '17. Put together a toolbox for common repairs around the house.  Be sure the toolbox and tools are stored safely.', '524', '');
INSERT INTO `RequirementConfig` VALUES ('5341', '', '1', 'Do the following:', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5342', '', '2', '1. Read Bernoulli\'s Principle. Show how it works.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5343', '', '3', '2. Read Pascal\'s Law. Tell about some inventions that use Pascal\'s law.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5344', '', '4', '3. Read Newton\'s first law of motion. Show in three different ways how inertia works.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5345', '', '5', '4. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Science.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5346', '', '6', 'Do six of the following:', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5347', '', '7', '5. Show the effects of atmospheric pressure.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5348', '', '8', '6. Show the effects of air pressure.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5349', '', '9', '7. Show the effects of water pressure. This may be combined with atmospheric pressure or with air pressure.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5350', '', '10', '8. With adult supervision, build and launch a model rocket. (NOTE: You must be at least 10 years old to work with a model rocket kit sold in stores.) Describe how Newton\'s third law of motion explains how the rocket is propelled into the sky.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5351', '', '11', '9. Explain what causes fog. Show how this works.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5352', '', '12', '10. Explain how crystals are formed. Make some.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5353', '', '13', '11. Explain how you use your center of gravity to keep your balance. Show three different balancing tricks.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5354', '', '14', '12. Show in three different ways how your eyes work together, and show what is meant by an optical illusion.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5355', '', '15', '13. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Weather.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5356', '', '16', '14. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Astronomy.', '525', '');
INSERT INTO `RequirementConfig` VALUES ('5357', '', '1', 'Do five of the following:', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5358', '', '2', '1. Make a map of the United States. Show the types of forests growing in different parts of the country. Name some kinds of trees that grow in these forests.', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5359', '', '3', '2. Draw a picture to show the plant and tree layers of a forest in your area. Label the different layers. (If you don\'t live in an area that has forests, choose an area that does and draw a picture of that forest.)', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5360', '', '4', '3. Identify six forest trees common to the area where you live. Tell how both wildlife and humans use them. (If you don\'t live in a region that has forests, read about one type of forest and name six of its trees and their uses.)', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5361', '', '5', '4. Identify six forest plants (other than trees) that are useful to wildlife. Tell which animals use them and for what purposes.', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5362', '', '6', '5. Draw a picture showing', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5363', '', '7', '    -how water and minerals in the soil help a tree grow', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5364', '', '8', '    -how the tree uses sunlight to help it grow', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5365', '', '9', '6. Make a poster showing a tree\'s growth rings or examine the growth rings of a tree stump. Explain how the rings tell its life history', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5366', '', '10', '7. Collect pieces of three kinds of wood used for building houses. Tell what kinds of wood they are and one place each of them might be used', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5367', '', '11', '8. Plant 20 forest tree seedlings. Tell how you planted them and what you did to take care of them after planting.', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5368', '', '12', '9. Describe both the benefits and the harm wildfires can cause in a forest ecosystem. Tell how you can prevent wildfire.', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5369', '', '13', '10. Draw your own urban forestry plan for adding trees to a street, yard, or park near your home. Show what types of trees you would like to see planted.', '526', '');
INSERT INTO `RequirementConfig` VALUES ('5370', '', '1', 'Do five of the following:', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5371', '', '2', '1. Collect five geologic specimens that have important uses.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5372', '', '3', '2. Rocks and minerals are used in metals, glass, jewelry, road-building products, and fertilizer. Give examples of minerals used in these products.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5373', '', '4', '3. Make a scale of mineral hardness for objects found at home. Show how to use the scale by finding the relative hardness of three samples.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5374', '', '5', '4. List some of the geologic materials used in building your home.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5375', '', '6', '5. Make a drawing that shows the cause of a volcano, a geyser, or an earthquake.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5376', '', '7', '6. Explain one way in which mountains are formed.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5377', '', '8', '7. Describe what a fossil is. How is it used to tell how old a formation is? Find two examples of fossils in your area.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5378', '', '9', '8. Take a field trip to a geological site, geological laboratory, or rock show. Discuss what you learned at your next Webelos den meeting.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5379', '', '10', '9. While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Geology.', '527', '');
INSERT INTO `RequirementConfig` VALUES ('5380', '', '1', 'Do the following:', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5381', '', '2', '1.   With your parent, guardian, or Webelos den leader, complete the Respect Character Connection.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5382', '', '3', '    a. Know: Tell what interested you most when completing the requirements for this activity badge. Tell what you learned about how you can show appreciation and respect for wildlife.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5383', '', '4', '    b. Commit: Tell things that some people have done that show a lack of respect for wildlife. Name ways that you will show respect for and protect wildlife.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5384', '', '5', '    c. Practice: Explain how completing the requirements for this activity badge gives you the opportunity to show respect.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5385', '', '6', 'Do five of the following:', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5386', '', '7', '2.   Keep an \'insect zoo\' that you have collected. You might have crickets, ants, or grasshoppers. Study them for a while then release them. Share your experience with your Webelos den.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5387', '', '8', '3.   Set up an aquarium or terrarium. Keep it for at least a month. Share your experience with your Webelos den by showing them photos or drawings of your project, or having them visit to see your project.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5388', '', '9', '4.   Visit a museum of natural history, nature center, or zoo with your family, Webelos den, or pack. Tell what you saw.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5389', '', '10', '5.   Watch for birds in your yard, neighborhood, or town for one week. Identify the birds you see and write down where and when you saw them.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5390', '', '11', '6.   Learn about the bird flyways closest to your home. Find out which birds use these flyways.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5391', '', '12', '7.   Learn to identify poisonous plants and venomous reptiles found in your area.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5392', '', '13', '8.   Watch six wild animals (snakes, turtles, fish, birds, or mammals) in the wild. Describe the kind of place (forest, field, marsh, yard, or park) where you saw them. Tell what they were doing.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5393', '', '14', '9.   Give examples of', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5394', '', '15', '    -A producer, a consumer, and a decomposer in the food chain of an ecosystem', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5395', '', '16', '    -One way humans have changed the balance of nature', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5396', '', '17', '    -How you can help protect the balance of nature', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5397', '', '18', '10.   Identify a plant, bird, or wild animal that is found only in your area of the country. Tell why it survives only in your area.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5398', '', '19', '11.   Learn about aquatic ecosystems and wetlands in your area. Discuss with your Webelos den leader or activity badge counselor the important role aquatic ecosystems and wetlands play in supporting lifecycles of wildlife and humans.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5399', '', '20', '12.   Look around your neighborhood and identify how litter might be dangerous to the birds and other animals. Clean up the litter. Identify what else you might do to make your neighborhood safer for animals.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5400', '', '21', '13.   While you are a Webelos Scout, earn the Cub Scout Academics belt loop for Wildlife Conservation.', '528', '');
INSERT INTO `RequirementConfig` VALUES ('5401', '', '1', 'Do two of the following:', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5402', '', '2', '1. Present yourself to your Webelos den leader, properly dressed, as you would be for an overnight campout. Show the camping gear you will use. Show the right way to pack and carry it.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5403', '', '3', '2. With your family or Webelos den, help plan and take part in an evening outdoor activity that includes a campfire.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5404', '', '4', '3. With your parent or guardian, take part in a Webelos den overnight campout or a family campout. Sleep in a tent that you have helped pitch.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5405', '', '5', '4. With your parent or guardian, camp overnight with a Boy Scout troop. Sleep in a tent that you have helped pitch.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5406', '', '6', 'Do five of the following:', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5407', '', '7', '5. During a Webelos den meeting, discuss how to follow the Leave No Trace Frontcountry Guidelines during outdoor activities (See page 72.)', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5408', '', '8', '6. Participate in an outdoor conservation project with your Webelos den or a Boy Scout troop.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5409', '', '9', '7. Discuss with your Webelos den leader the rules of outdoor fire safety. Using these rules, show how to build a safe fire and put it out.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5410', '', '10', '8. With your accompanying adult on a campout or outdoor activity, assist in preparing, cooking, and cleanup for one of your den\'s meals. Tell why it is important for each den member to share in meal preparation and cleanup, and explain the importance of eating together.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5411', '', '11', '9. Discuss with your Webelos den leader the things that you need to take on a hike. Go on one 3-mile hike with your Webelos den or a Boy Scout troop.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5412', '', '12', '10. Demonstrate how to whip and fuse the ends of a rope.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5413', '', '13', '11. Demonstrate setting up a tent or dining fly using two half hitches and a taut-line hitch. Show how to tie a square knot and explain how it is used.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5414', '', '14', '12. Visit a nearby Boy Scout camp with your Webelos den.', '529', '');
INSERT INTO `RequirementConfig` VALUES ('5415', '', '2', 'Complete the following three requirements:', '530', '');
INSERT INTO `RequirementConfig` VALUES ('5416', '', '3', '1. Make a list of common materials used to create visual art compositions.', '530', '');
INSERT INTO `RequirementConfig` VALUES ('5417', '', '4', '2. Demonstrate how six of the following elements of design are used in a drawing: lines, circles, dots, shapes, colors, patterns, textures, space, balance, or perspective.', '530', '');
INSERT INTO `RequirementConfig` VALUES ('5418', '', '5', '3. Identify the three primary colors and the three secondary colors that can be made by mixing them. Show how this is done using paints or markers. Use the primary and secondary colors to create a painting', '530', '');
INSERT INTO `RequirementConfig` VALUES ('5419', '', '7', 'Earn the Art belt loop, and complete six of the following requirements:', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5420', '', '8', '1. Visit an art museum, gallery, or exhibit. Discuss with an adult the art you saw.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5421', '', '9', '2. Create two self-portraits using two different art techniques, such as drawing, painting, printmaking, sculpture, or computer illustration.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5422', '', '10', '3. Demonstrate how to make paper. Make a sample at least 4 inches by 4 inches.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5423', '', '11', '4. Make a simple silkscreen or stencil. Print a card or T-shirt.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5424', '', '12', '5. Create a freestanding sculpture or mobile using wood, metal, soap, papier-mache, or found objects.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5425', '', '13', '6. Create an object using clay that can be fired, baked in the oven, or hardened in water.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5426', '', '14', '7. Photograph four subjects in one theme, such as landscapes, people, animals, sports, or buildings.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5427', '', '15', '8. Make a collage using several different materials.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5428', '', '16', '9. Use your artistic skills to create a postage stamp, book cover, or music CD cover.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5429', '', '17', '10. Use a computer illustration or painting program to create a work of art.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5430', '', '18', '11. Display your artwork in a pack, school, or community art show.', '550', '');
INSERT INTO `RequirementConfig` VALUES ('5431', '', '2', 'Complete the following three requirements:', '531', '');
INSERT INTO `RequirementConfig` VALUES ('5432', '', '3', '1. Set up and demonstrate how to focus a simple telescope or binoculars. (A local astronomy club may be a resource for this activity.)', '531', '');
INSERT INTO `RequirementConfig` VALUES ('5433', '', '4', '2. Draw a diagram of our solar system--identify the planets and other objects.', '531', '');
INSERT INTO `RequirementConfig` VALUES ('5434', '', '5', '3. Explain the following terms: planet, star, solar system, galaxy, the Milky Way, black hole, red giant, white dwarf, comet, meteor, moon, asteroid, star map, and universe.', '531', '');
INSERT INTO `RequirementConfig` VALUES ('5435', '', '1', 'Earn the Astronomy belt loop, and complete five of the following requirements:', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5436', '', '2', '1. Draw a diagram of a telescope and explain how it works.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5437', '', '3', '2. Explain how to use a star map.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5438', '', '4', '3. Draw and label five constellations. See if you can locate any of them in the sky using a star map.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5439', '', '5', '4. Find the North Star. Explain its importance.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5440', '', '6', '5. With your parent\'s or adult partner\'s permission, interview an astronomer. This person may be a professional or an amateur astronomer from a local astronomy club. Report on what you learned to your den or family.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5441', '', '7', '6. Learn about careers that relate to Astronomy. Make a list of those careers. Tell your den or an adult family member what school subjects will help you get a position in those careers.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5442', '', '8', '7. Visit a planetarium or a local astronomy club. Give a report on what you learned to your den.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5443', '', '9', '8. Make a poster illustrating the different kinds of stars. Include a diagram showing the life cycle of a star.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5444', '', '10', '9. Learn about some of the early space missions. Tell your den or family about one of them.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5445', '', '11', '10. Find a news story about a recent happening related to space. Tell your den or family about this event.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5446', '', '12', '11. Write a report on two famous astronomers.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5447', '', '13', '12. Locate three major observatories on a map. Explain why these locations are good for astronomy.', '551', '');
INSERT INTO `RequirementConfig` VALUES ('5448', '', '2', 'Complete these three requirements:', '532', '');
INSERT INTO `RequirementConfig` VALUES ('5449', '', '3', '1. Identify the chess pieces and set up a chess board for play.', '532', '');
INSERT INTO `RequirementConfig` VALUES ('5450', '', '4', '2. Demonstrate the moves of each chess piece to your den leader or adult partner.', '532', '');
INSERT INTO `RequirementConfig` VALUES ('5451', '', '5', '3. Play a game of chess.', '532', '');
INSERT INTO `RequirementConfig` VALUES ('5452', '', '7', 'Earn the Chess belt loop, and complete five of the following requirements:', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5453', '', '8', '1. Demonstrate basic opening principles (such as development of pieces, control center, castle, don\'t bring queen out too early, don\'t move same piece twice).', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5454', '', '9', '2. Visit a chess tournament and tell your den about it.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5455', '', '10', '3. Participate in a pack, school, or community chess tournament.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5456', '', '11', '4. Solve a pre-specified chess problem (e.g., \'White to move and mate in three\') given to you by your adult partner.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5457', '', '12', '5. Play five games of chess.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5458', '', '13', '6. Play 10 chess games via computer or on the Internet.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5459', '', '14', '7. Read about a famous chess player. Tell your den or an adult family member about that player\'s life.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5460', '', '15', '8. Describe U.S. Chess Federation ratings for chess players.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5461', '', '16', '9. Learn to write chess notation and record a game with another Scout.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5462', '', '17', '10. Present a report about the history of chess to your den or family.', '552', '');
INSERT INTO `RequirementConfig` VALUES ('5463', '', '2', 'Complete these three requirements:', '533', '');
INSERT INTO `RequirementConfig` VALUES ('5464', '', '3', '1. Develop a list of jobs you can do around the home. Chart your progress for one week.', '533', '');
INSERT INTO `RequirementConfig` VALUES ('5465', '', '4', '2. Make a poster showing things that you can do to be a good citizen.', '533', '');
INSERT INTO `RequirementConfig` VALUES ('5466', '', '5', '3. Participate in a family, den, or school service project.', '533', '');
INSERT INTO `RequirementConfig` VALUES ('5467', '', '0', 'Earn the Citizenship belt loop, and complete five of the following requirements:', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5468', '', '1', '1. Interview someone who has become a naturalized citizen. Give a report of your interview to your den or family.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5469', '', '2', '2. Write a letter to your newspaper about an issue that concerns you. Send your letter by mail or e-mail.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5470', '', '3', '3. Create a collage about America.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5471', '', '4', '4. Make a list of items to check for a home safety or energy audit and then inspect your home. Talk with your parent or adult partner about correcting any problems you find.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5472', '', '5', '5. Visit your local site of government. Interview someone who is involved with the governmental process.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5473', '', '6', '6. Visit a courtroom and talk with someone who works there.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5474', '', '7', '7. Go to the polls with your parents when they vote. Talk to them about their choices.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5475', '', '8', '8. Take part in a parade with your den or pack.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5476', '', '9', '9. List ways you can recycle various materials and conserve and protect the environment.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5477', '', '10', '10. Attend a community event or visit a landmark in your community.', '553', '');
INSERT INTO `RequirementConfig` VALUES ('5478', '', '2', 'Complete these three requirements:', '534', '');
