INSERT INTO `RequirementConfig` VALUES ('5499', '', '7', 'Earn the Computers belt loop, and complete five of the following requirements:', '555', '');
INSERT INTO `RequirementConfig` VALUES ('5500', '', '8', '1. Use a computer to prepare a report on a subject of interest to you. Share it with your den.', '555', '');
INSERT INTO `RequirementConfig` VALUES ('5501', '', '9', '2. Make a list of 10 devices that can be found in the home that use a computer chip to function.', '555', '');
