INSERT INTO `RequirementConfig` VALUES ('7159', '', '34', '  B. Discover. Explain to your counselor the difference between escape velocity (not the game), orbital velocity, and terminal velocity. Then answer TWO of the following questions. (With your parents or guardians permission, you may explore websites to find this information.)', '693', '');
INSERT INTO `RequirementConfig` VALUES ('7160', '', '35', '    1. Why are satellites usually launched toward the east, and what is a launch window?', '693', '');
